+              <Route path='/portfolio/profileinfo' element={<ProfileInformation />}></Route>
+              
+
+              {/*  */}
+              <Route path='/portfolio/dynamic-timetable' element={<DynamicTimeTable />}></Route>
+              <Route path='/portfolio/learning-network' element={<LearningNetwork />}></Route>
+
+            </Route>
+
+            <Route path='/lifeskillspage' element={<LifeskillsPage />}></Route>
+            <Route path='/sportspage' element={<SportsPage />}></Route>
+            <Route path='/dashboard' element={<DashboardComponent updateLoginStatus={updateLoginStatus} />}></Route>
+            <Route path='/takelesson' element={<TakeaClassPage />}>
+              <Route index element={<VideoWithPoll videoRef={videoRef} />}></Route>
+              <Route path='/takelesson/lecture/:url' element={<VideoWithPoll videoRef={videoRef} />}></Route>
+              <Route path='/takelesson/syllabus of topic/:url' element={<Pdf />}></Route>
+              <Route path='/takelesson/definitions/:url' element={<Ppt />}></Route>
+              <Route path='/takelesson/learning through gaming/:url' element={<GamePlayer />}></Route>
+            </Route>
+            <Route path='/levels' element={<LevelsComponent />}></Route>
+            <Route path='/skills' element={<InstructionsScreen />}></Route>
