commits.content:
.languages {
  min-width: 98vw;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  background: transparent linear-gradient(115deg, #e6eee6 0%, #eae4f9 100%);

  header {
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0rem;
    text-align: center;

    h1 {
      font-size: 1.5rem;
      line-height: 1.75rem;
      font-weight: 600;
      margin-bottom: 0.5rem;

      @media (min-width: 768px) {
        font-size: 2rem;
      }
    }

    p {
      color: #94a3b8;
    }
  }
}



.tagList {
  width: 98.8vw;
  max-width: 100vw;
  display: flex;
  flex-shrink: 0;
  flex-direction: column;
  gap: 1rem;
  position: relative;
  padding: 1.5rem 0;
  overflow: hidden;
}

.loopSlider {
  .inner {
    display: flex;
    width: fit-content;
    animation-name: loop;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
    animation-direction: var(--direction);
    animation-duration: var(--duration);
  }
}

.loopSlider .inner {
  display: flex;
  width: fit-content;
  animation: loop linear infinite;
  animation-direction: var(--direction);
  animation-duration: var(--duration);
}

.tag {
  display: flex;
  align-items: center;
  gap: 0 0.2rem;
  color: #ffffff;
  font-size: 0.9rem;
  background-color: #35008b;
  border-radius: 0.4rem;
  padding: 0.7rem 1rem;
  margin-right: 1rem;
  box-shadow: 0 0.1rem 0.2rem rgba(0, 0, 0, 0.2), 0 0.1rem 0.5rem rgba(0, 0, 0, 0.3), 0 0.2rem 1.5rem rgba(0, 0, 0, 0.4);

  span {
    font-size: 1.2rem;
    color: #dcdcdc;
  }
}

.fade {
  pointer-events: none;
  // background: linear-gradient(90deg, #1e293b, transparent 20%, transparent 75%, #1e293b);
  position: absolute;
  inset: 0;
}

@keyframes loop {
  0% {
    transform: translateX(0);
  }

  100% {
    transform: translateX(-50%);
  }
}
