// Tab switch styles
.tab-switch {
  position: relative;
  background: #6617e7;
  border-radius: 3rem;
  width: 27vw;
  overflow: hidden;
}

.tab-switch:after {
  content: "";
  position: absolute;
  width: 33.3333%;
  top: 0;
  transition: left cubic-bezier(.88, -.35, .565, 1.35) .4s;
  border-radius: 27.5px;
  box-shadow: 0 2px 15px 0 rgba(0, 0, 0, .1);
  background-color: white;
  height: 100%;
  z-index: 0;
}

.tab-switch.left:after {
  left: 0;
}

.tab-switch.center:after {
  left: 33.3333%; // Adjusted for 3 tabs
}

.tab-switch.right:after {
  left: 66.6667%; // Adjusted for 3 tabs
}

.tab-switch .tab {
  display: inline-block;
  width: 33.3333%;
  padding: 12px 0;
  z-index: 1;
  position: relative;
  cursor: pointer;
  transition: color 200ms;
  font-size: .875rem;
  font-weight: 600;
  line-height: 1rem;
  user-select: none;
  color: white; // Inactive tab color
  text-align: center;
  text-decoration: none;
}

.tab-switch .tab:hover {
  // color: rgba(122, 128, 255, 0.9); // Hover effect
}

.tab-switch .tab.active {
  color: black;
  
}
