$inputboxcolor: #F0EDFFCC;
$animation: #7c69f2;
$primarycolor: white;
$stepscolor: #14cc57;
$textcolor: black;

.signup {
    width: 100%;
    // background: transparent linear-gradient(115deg,#e6eee6 0%,#eae4f9 100%);
    // background: linear-gradient(115deg, #e6eee6, #eae4f9);
    background-color: #35008b;
    // background-image: url('../../assets/swril-blue.svg');
    // background-size: cover;
    // background-position: top right;
    // animation: rotate 10s linear infinite; /* Adjust the duration and timing function as needed */
    min-height: 100vh;

    &__background {
        position: fixed;
        top: 0rem;
        animation: rotate 39s linear infinite;
        right: 12rem;

        .swirlbackground {
            transform: scale(1.2);
        }

    }

    @keyframes rotate {
        from {
            transform: rotate(0deg);
        }

        to {
            transform: rotate(360deg);
        }
    }

    &__branelogo {
        img {
            width: 19rem;
            padding: 1rem;
        }
    }

    &__container {
        width: 100%;
        display: flex;
        flex-direction: row;
        align-content: center;
        justify-content: center;
        align-items: center;
        height: 36vw;

        &__form {
            background-color: white;
            width: 45%;
            padding: 1rem 0rem 0rem 1rem;
            display: flex;
            flex-direction: column;
            /* filter: drop-shadow(0 3px 6px rgba(0, 0, 0, 0.0588235294)); */
            border-radius: 1rem;
            /* border-style: ridge; */
            /* border: 1.4px solid; */
            border-color: #474747;
            box-shadow: 0px 2px 13px 4px rgb(176 176 176 / 48%);
            z-index: 3;

            &__animation {
                flex: 1;
                height: 100%;
                border-radius: 8px;
                overflow: hidden;
                position: relative;
                display: flex;
                justify-content: center;
                align-items: center;
                margin-top: -1rem;

                & .progress-container {
                    position: relative;
                    margin: 2rem auto 2rem;
                    padding: 4px 0 0;
                    min-width: 21vw;
                    height: 6px;
                    background: #747272;
                    box-shadow: inset 0 0 2px #666;

                    ul {
                        list-style: none;
                        margin: -4px;
                        padding: 0;

                        li {
                            position: absolute;
                            top: -12px;
                            left: 0;
                            margin: 0;
                            padding: .3rem .65rem;
                            display: inline-table;
                            height: 32px;
                            width: 32px;
                            color: $primarycolor;
                            background: $stepscolor;
                            border-radius: 50%;
                        }
                    }

                    & .progress-bar {
                        height: 6px;
                        width: 0%;
                        background: $stepscolor;
                        transition: width 0.9s ease-in-out;
                    }
                }
            }

            &__logo {
                img {
                    width: 12vw;
                }
            }

            &__div {

                &__resendotp {
                    font-size: .75rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                }

                &__form {
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;

                    &__heading {
                        font-size: 1.5rem;
                        padding: 0.25rem 0;
                    }

                    &__sec {
                        // min-height: 8.75rem;
                        overflow-y: auto;
                        min-width: 100%;
                        // max-width: 75%;
                        padding: 0 4.35rem 0.5rem 4rem;
                        margin: 0 auto;
                        position: relative;
                        // min-height: 21vh;




                        &::-webkit-scrollbar {
                            width: 5px;
                        }

                        &::-webkit-scrollbar-track {
                            background: #C6C4D2;
                        }

                        /* Handle */
                        &::-webkit-scrollbar-thumb {
                            background: #3B3B3B;
                            border-radius: 5px;
                        }

                        /* Handle on hover */
                        &::-webkit-scrollbar-thumb:hover {
                            background: #3B3B3B;
                        }

                        &__main {
                            width: 100%;
                        }

                        &__title {
                            display: flex;
                            justify-content: space-between;
                            // margin-top: -11px;

                            div:nth-child(1) {
                                font-size: 1.1rem;
                                color: black;
                                // padding: 0.5rem 0;
                                margin-left: 13rem;
                            }

                            div:nth-child(2) {
                                button {
                                    // margin: 1rem 1rem 0 0;
                                    // margin-top: -11px;
                                    padding: 0.5rem;
                                    background: #e15575;
                                    color: $primarycolor;
                                    border: none;
                                    border-radius: 0.5rem;
                                    cursor: pointer;
                                }
                            }
                        }

                        &__input-container {
                            display: flex;
                            // width: 60%;
                            padding: .5rem 0;

                            & .dob-icon {
                                padding: .75rem 0;
                                background: $inputboxcolor;
                                color: black;
                                min-width: 50px;
                                text-align: center;
                                border-top-left-radius: .5rem;
                                border-bottom-left-radius: .5rem;
                            }

                            & .icon {
                                padding: 0.65rem 0;
                                font-size: 0.845rem;
                                background: $inputboxcolor;
                                color: black;
                                min-width: 40px;
                                text-align: center;
                                border-top-left-radius: .5rem;
                                border-bottom-left-radius: .5rem;
                            }

                            .password-icon {
                                padding: 0.55rem 0;
                                font-size: 0.9rem;
                                background: $inputboxcolor;
                                color: black;
                                min-width: 40px;
                                text-align: center;
                                border-top-right-radius: .5rem;
                                border-bottom-right-radius: .5rem;
                            }

                            &__input-field {
                                width: 100%;
                                padding: 0.65rem 0;
                                font-size: 0.845rem;
                                border: none;
                                outline: none;
                                background-color: $inputboxcolor;
                                border-top-right-radius: .5rem;
                                border-bottom-right-radius: .5rem;

                                &-password {
                                    width: 100%;
                                    padding: 0.35rem 0;
                                    font-size: 0.845rem;
                                    border: none;
                                    outline: none;
                                    background-color: $inputboxcolor;
                                    border-top-right-radius: 0.5rem;
                                    border-bottom-right-radius: 0.5rem;
                                }
                            }

                            input[type="date"] {
                                padding: .75rem;
                            }

                            select {
                                font-size: .845rem;
                                color: #000000;
                                font-weight: 400;
                                cursor: pointer;

                                option {
                                    font-size: .845rem;
                                    color: black;
                                    background-color: $inputboxcolor;
                                    margin: 5rem;
                                }
                            }
                        }

                        .error {

                            // padding-left: 2.5rem;
                            // position: absolute;
                            // z-index: 111;
                            // margin-top: -9px;
                            // font-size: 0.745rem;
                            &-pmessage {
                                padding-left: 8px;
                            }
                        }

                        small {
                            color: red;
                            padding-left: 0.5rem;
                            position: absolute;
                            z-index: 111;
                            margin-top: -9px;
                            font-size: 0.745rem;
                        }
                    }

                    .step1container {
                        min-width: 78%;
                        min-height: 11vh;
                    }

                    .step2container {
                        min-width: 78%;
                        min-height: 21vh;
                    }

                    &__buttons {
                        width: 70%;
                        display: flex;
                        justify-content: space-between;
                    }


                }

                &__button {
                    padding: 0.35rem 1.25rem;
                    font-size: .9rem;
                    background-color: #4130AE;
                    color: $primarycolor;
                    border-radius: 0.75rem;
                    border: none;
                    cursor: pointer;
                    text-decoration: none;

                    &:disabled {
                        background-color: #a69ed6;
                        cursor: not-allowed;
                    }
                }

                &__geolocationbtn {
                    background-color: $primarycolor;
                    padding: .35rem 1rem;
                    font-size: .845rem;
                    color: $textcolor;
                    border: 2px solid $inputboxcolor;
                    margin: 0.5rem 0;
                    cursor: pointer;
                    width: 100%;
                    // font-size: .9rem;

                }


                &__steps {
                    p {
                        text-align: center;
                        padding: 0rem;
                        font-size: 1.25rem;
                        margin-bottom: 0;
                    }

                    &-indicator {
                        display: flex;
                        justify-content: center;
                        padding-bottom: 1rem;
                    }

                }
            }
        }

        &__animation {
            width: 50%;
        }
    }
}

.haveanaccount {
    text-align: center;
    padding: 1rem 1rem 0.25rem;
    font-size: .875rem;
    font-weight: 400;
    line-height: 1.1875rem;

    a {
        text-decoration: none;
        color: #35008b;
        font-weight: 600;
    }
}

.FooterCopyright {
    font-size: .75rem;
    line-height: 1rem;
    font-weight: 400;
    color: #35008b;
    padding-left: 1rem;
    position: fixed;
    top: 45vw;
}


.signupform-btn {
    font-size: .875rem;
    padding: 0.5rem 1.7rem;
    font-weight: 500;
    background: #0004be;
    color: white;
    border: none;
    position: relative;
    overflow: hidden;
    border-radius: 0.6em;
    cursor: pointer;
    transition: transform 250ms;

    :disabled {
        background: #aaaaaa;
    }

    .gradient {
        position: absolute;
        width: 100%;
        height: 100%;
        left: 0;
        top: 0;
        border-radius: 0.6em;
        margin-top: -0.25em;
        background-image: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.3));
    }

    .label {
        position: relative;
        top: -1px;
    }

    .transition {
        transition-timing-function: cubic-bezier(0, 0, 0.2, 1);
        transition-duration: 500ms;
        background-color: #6617e7;
        border-radius: 9999px;
        width: 0;
        height: 0;
        position: absolute;
        left: 50%;
        top: 50%;
        transform: translate(-50%, -50%);
    }

    &:hover {
        .transition {
            width: 14em;
            height: 14em;
        }
    }

    &:active {
        transform: scale(0.97);
    }

    &:disabled {
        background: #aaaaaa;
        cursor: not-allowed;

        .label {
            color: white;
        }
    }
}





.signup__container__form__div__form__sec__input-container {
    position: relative;
    width: 100%;
}

.signup__container__form__div__form__sec__input-container__dobinput-field {
    box-sizing: border-box;
    -webkit-appearance: none;
    appearance: none;
    display: flex;
    gap: .625rem;
    flex-direction: row-reverse;
    // margin-left: -40px;

    width: 100%;
    padding: 0.65rem 0;
    font-size: 0.845rem;
    border: none;
    outline: none;
    background-color: $inputboxcolor;
    border-radius: .5rem;
}

