.circular-border {
    width: 200px;
    /* Adjust the width and height to match your video's dimensions */
    height: 200px;
    border-radius: 50%;
    /* Makes the container circular */
    overflow: hidden;
    /* Hides the parts of the webcam video outside the circle */
    display: inline-block;
    /* Ensures the circular border doesn't take up extra space */
    position: relative;
    /* Needed for positioning the video */
}

.circular-border video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    /* Ensures the video fills the circular container */
    transform: scaleX(-1);
    /* Flip the video horizontally if needed */
}
